        if (input.trim()) {
            onSendMessage(input);
            setInput("");
        }
    };

    return (
        <div className="input-box">
            <input value={input} onChange={(e) => setInput(e.target.value)} placeholder="Ask something..." />
            <button onClick={handleSend}>â¬†</button>
        </div>
    );
}

