import React, { useState } from "react";
import axios from "axios";
import Message from "./Message";
import InputBox from "./InputBox";
import "Chatbot.css"


function Chatbot() {
    const [messages, setMessages] = useState([
        { text: "Hey there! How can I help you?", sender: "bot" },
    ]);

    const sendMessage = async (text) => {
        const newMessages = [...messages, { text, sender: "user" }];
        setMessages(newMessages);

        try {
            const response = await axios.post("http://127.0.0.1:5000/chat", { query: text });
            setMessages([...newMessages, { text: response.data.response, sender: "bot" }]);
        } catch (error) {
            setMessages([...newMessages, { text: "Error fetching response", sender: "bot" }]);
        }
    };

    return (
        <div className="chatbot">
            <h2>Chatbot</h2>
            <div className="messages">
                {messages.map((msg, index) => (
                    <Message key={index} text={msg.text} sender={msg.sender} />
                ))}
            </div>
            <InputBox onSendMessage={sendMessage} />
        </div>
    );
}

export default Chatbot;
